package com.aayush.shoppingapp.common.helperimport android.app.Activityimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentTransactionimport com.aayush.shoppingapp.views.SettingsActivityfun Fragment.navigateToScreen(container: Int, fragment: Fragment, backStackKey: String) {    parentFragmentManager.beginTransaction()        .replace(container, fragment)        .addToBackStack(backStackKey)        .setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE)        .commit()}fun AppCompatActivity.navigateToScreen(container: Int, fragment: Fragment) {    supportFragmentManager.beginTransaction()        .replace(container, fragment)        .setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE)        .commit()}fun AppCompatActivity.navigateToScreen(currentActivity: AppCompatActivity, destinationActivity: Class<SettingsActivity>) {    val intent = Intent(currentActivity, destinationActivity)    startActivity(intent)}