package com.aayush.shoppingapp.common.helperimport android.content.Contextimport android.content.Context.MODE_PRIVATEimport android.content.SharedPreferencesimport android.view.Display.Modeclass ThemeManager(val context: Context) {    private val SharedPreferenceDB = "SharedPreferenceDB"    private val Preference_Name = "DarkThemeManager"    val sharedPref: SharedPreferences =        context.getSharedPreferences(SharedPreferenceDB, MODE_PRIVATE)    val editor: SharedPreferences.Editor = sharedPref.edit()    fun saveTheme(isDarkMode: Boolean) {        editor.putBoolean(Preference_Name, isDarkMode)        editor.apply()    }    fun isDarkThemeEnabled(): Boolean {        return sharedPref.getBoolean(Preference_Name, false)    }}