package com.aayush.shoppingapp.use_caseimport com.aayush.shoppingapp.Repository.CategoryRepositoryimport com.aayush.shoppingapp.UIStateimport com.aayush.shoppingapp.models.CategoryModelimport javax.inject.Injectclass CategorySaveUseCase @Inject constructor(val categoryRepository: CategoryRepository) {    suspend fun execute(categoryModel: CategoryModel): UIState<Nothing?> {        return categoryRepository.addNewCategory(categoryModel)    }}